def calculate_memory_size(array_str):
    """Calculate memory size for an array of const int16_t"""
    data_type_size = 2  # int16_t is 2 bytes

    # Clean up the input (remove C-style braces, spaces, and newlines)
    array_str = array_str.strip().replace("{", "").replace("}", "").replace(" ", "").replace("\n", "")

    # Extract array elements
    elements = array_str.split(",")

    # Remove empty strings caused by trailing commas (if any)
    elements = [e for e in elements if e]

    # Count elements
    num_elements = len(elements)

    # Calculate memory size
    memory_size = num_elements * data_type_size

    print(f"Array contains {num_elements} elements.")
    print(f"Each element is of type const int16_t ({data_type_size} bytes).")
    print(f"Total memory used: {memory_size} bytes.")

